  const [showArrangeBoard,setArrangeBoard] = useState(true);

  const setShowStartFalse = () => {
    setShowStart(false);
  };

  const setShowArrangeBoard = () => {
    setArrangeBoard(true);
  };  


  return (
    <div className="App">
      <div className="title">BATTLESHIP</div>
      {showStart ? <StartGame setShowStartFalse={setShowStartFalse} setShowArrangeBoardTrue={setShowArrangeBoardTrue}/> : null}
    </div>
  );
}

export default App;
